function onLoad(saved_data)
    self.createInput({input_function = "script_livro", function_owner = self, label = "Script", position = {0, 0.15, 0}, rotation = {0, 0, 0}, scale = {1, 1, 1}, width = 900, height = 330, font_size = 300, tooltip = "Script", alignment = 3, color = {0,0,0}, font_color = {1,1,1}, validation = 2, tab = 1})
end

function script_livro(botao, jogador, valor, selecionado)
    valor = tonumber(valor)
    if valor ~= nil then
        if selecionado == false and valor >= 1 and SCRIPTS[valor] ~= nil then
            broadcastToAll(SCRIPTS[valor] .. "\n\n\n", {1, 1, 1})
            self.setDescription(SCRIPTS[valor])
        end
        return valor
    end
end

